select  i_item_id, s_state,avg(ss_quantity) agg1,avg(ss_list_price) agg2,avg(ss_coupon_amt) agg3,avg(ss_sales_price) agg4 from TPCDS_QUERY27_5G.STORE_SALES ss JOIN TPCDS_QUERY27_5G.CUSTOMER_DEMOGRAPHIES cd ON ss.ss_cdemo_sk = cd.cd_demo_sk JOIN TPCDS_QUERY27_5G.DATE_DIM dd ON ss.ss_sold_date_sk = dd.d_date_sk JOIN TPCDS_QUERY27_5G.STORE st ON ss.ss_store_sk = st.s_store_sk JOIN TPCDS_QUERY27_5G.ITEM it ON ss.ss_item_sk = it.i_item_sk group by i_item_id, s_state
