select  i_item_id, avg(ss_quantity) agg1,  avg(ss_list_price) agg2,  avg(ss_coupon_amt) agg3,  avg(ss_sales_price) agg4 from  tpcds_5g_query7.store_sales AS ss JOIN tpcds_5g_query7.customer_demographies as cd on ss.ss_cdemo_sk = cd.cd_demo_sk JOIN tpcds_5g_query7.date_dim as dd on ss.ss_sold_date_sk = dd.d_date_sk JOIN tpcds_5g_query7.item as it on ss.ss_item_sk = it.i_item_sk JOIN tpcds_5g_query7.TPCDS_5G_PROMOTION as p  on ss.ss_promo_sk = p.p_promo_sk group by i_item_id  order by i_item_id
