select st.s_city,sum(ss_coupon_amt) amt,sum(ss_net_profit) profit from TPCDS_QUERY46_5G.STORE_SALES ss JOIN TPCDS_QUERY46_5G.DATE_DIM dd ON ss.ss_sold_date_sk = dd.d_date_sk JOIN TPCDS_QUERY46_5G.STORE st ON ss.ss_store_sk = st.s_store_sk JOIN TPCDS_QUERY46_5G.HOUSEHOLD_DEMOGRAPHICS hd ON 	ss.ss_hdemo_sk = hd.hd_demo_sk where hd.hd_dep_count = 8 or hd.hd_vehicle_count > 0 group by st.s_city
